import React, { useRef } from 'react';
import Tab from './tab';
import { useStore } from 'effector-react';
import { ITab } from '../../../data/tabs';
import { $tabs, $currentTab, changeTab }  from '../../../model/tabs';
import { StyledTabs, TabsNav } from './styles';

const Tabs: React.FC = () => {
  const tabs = useStore($tabs);
  const currentTab = useStore($currentTab);

  const ref = useRef(null);

  return (
    <div ref={ref}>
      <StyledTabs>
        <h2 className='visually-hidden'>Сервисы</h2>
        <TabsNav>
          {tabs.map((tab) => (
            <li key={tab.id}>
              <button type='button' onClick={() => changeTab(tab.id)}>
                {tab.title}
              </button>
            </li>
          ))}
        </TabsNav>
        <Tab tab={tabs.find((tab) => tab.id === currentTab)}/>
      </StyledTabs>
    </div>
  )
};

export default Tabs;
