import React, { useEffect } from 'react';
import { useStore } from 'effector-react';
import {$products, filterByCategory, $categories, resetProducts} from '../../../model/products';
import { ProductCard } from '../../ui';
import { Products, ListItem, Text } from './styles';

const ProductsList: React.FC = () => {
  const products = useStore($products);
  const categories = useStore($categories);

  useEffect(() => {
    filterByCategory(categories.currentCategory.name);
  })

  return (
    <div>
      <h3 className='visually-hidden'>Список товаров</h3>
      {products?.length ? (
        <Products>
          {products.map((product) => (
            <ListItem key={product.id}>
              <ProductCard product={product} />
            </ListItem>
          ))}
        </Products>
      ) : <Text>К сожалению, таких товаров пока нет</Text>}
    </div>
  )
};

export default ProductsList;
