import styled from 'styled-components';
import { ICatalogNav } from './catalog-nav';

export const StyledNav = styled.div<ICatalogNav>`
  display: ${(props) => props.display ? 'block' : 'none'};
  background-color: ${(props) => props.theme.colors.themeMain};
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  padding: 0 60px;
`;
