import React from 'react';
import { StyledButton } from './styles';

export interface IButton {
  children: React.ReactNode;
  width?: string;
  marginTop?: number;
  marginBottom?: number;
  as?: any;
  className?: string;
  highlighted?: boolean;
  light?: boolean;
}

const Button: React.FC<IButton> =
  ({ children,
     width,
     marginTop,
     marginBottom,
     as,
     className,
     highlighted,
     light }) => {
  return (
    <StyledButton
      as={as}
      width={width}
      marginTop={marginTop}
      marginBottom={marginBottom}
      className={className}
      wide={wide}
      highlighted={highlighted}
      light={light}
      tab={tab}
    >
      {children}
    </StyledButton>
  )
};

export default Button;
