import styled from 'styled-components';

export const StyledCard = styled.div`
  display: grid;
  grid-template-columns: 1fr 100px;
  grid-template-areas: 
          'image image'
          'title price';
  row-gap: 10px;
`;

export const Image = styled.img`
  display: block;
  grid-area: image;
  position: relative;
`;
