import styled from 'styled-components';

interface IWrapper {
  background?: string;
};

export const StyledTabs = styled.section`
  background: linear-gradient(to bottom, 
    ${(props) =>props.theme.colors.basicWhite} 100px, 
    ${(props) => props.theme.colors.basicLight} 0);
  padding: ${(props) => props.theme.pagePadding};
  padding-top: 0;
  padding-bottom: 70px;
  display: grid;
  grid-template-columns: ${(props) => props.theme.layoutColumnWide} 1fr;
  align-items: center;
  column-gap: 120px;
  min-height: 380px;
`;

export const TabsNav = styled.ul`
  list-style-type: none;
  padding: 0;
`;

export const TabContent = styled.div`
  padding-right: 60px;
`;

export const Wrapper = styled.div<IWrapper>`
  padding-right: 200px;
  background-image: url(${(props) => props.background});
  background-repeat: no-repeat;
  background-position: bottom right;
`;
