import React, { ChangeEvent, useState } from 'react';
import { useStore } from "effector-react";
import { $filters, changeFilters } from "../../../model/catalog";
import {
  RangeScale,
  RangeBar,
  LeftBlock,
  RightBlock,
  LeftToggle,
  RightToggle,
  LeftLabel,
  RightLabel,
  Input
} from './styles';

export interface IRangeProps {
  width?: number;
  name: string;
  minValue: number;
  maxValue: number;
}

const Range: React.FC<IRangeProps> =
  ({ width,
     name,
     minValue,
     maxValue}) => {

  const [min, setMin] = useState(minValue);
  const [max, setMax] = useState(maxValue);
  const filters = useStore($filters);

  const handleChange = (evt: ChangeEvent<HTMLInputElement>, setter: (num: number) => any): void => {
    const value = Number(evt.target.value);
    const minRange = Number(evt.target.min);
    const maxRange = Number(evt.target.max);

    if (value < minRange) setter(minRange);
    else if (value > maxRange) setter(maxRange);
    else setter(value);

    const newPrice = {price: {min: min, max: max}};
    changeFilters({...filters, ...newPrice});
  };

    return (
    <RangeScale width={width}>
      <RangeBar left={min} right={max}>
        <LeftBlock>
          <LeftToggle type='button'/>
          <LeftLabel>
            от
            <Input
              type='number'
              min='0'
              max='7000'
              name={`min-${name}`}
              value={min}
              onChange={evt => handleChange(evt, setMin)}
            />
          </LeftLabel>
        </LeftBlock>

        <RightBlock>
          <RightToggle type='button' />
          <RightLabel>
            до
            <Input
              type='number'
              min='2000'
              max='10000'
              name={`max-${name}`}
              value={max}
              onChange={evt => handleChange(evt, setMax)}
            />
          </RightLabel>
        </RightBlock>
      </RangeBar>
    </RangeScale>
  )
};

export default Range;
