import React from 'react';
import { filters } from '../../../data/filters';
import { Title, Range } from '../../ui';
import {
  StyledFilters,
  Wrapper,
  Header,
  Form,
  Fieldset,
  List,
  ListItem,
  Control,
  Label
} from './styles';

const Filters: React.FC = () => {
  return (
    <StyledFilters>
      <Wrapper>
        <Header>
          <Title as='h3' size='S'>Фильтр:</Title>
        </Header>
        <Form action='#' method='get'>
          {
            filters.map((filter) => (
              <Fieldset>
                <Title as='legend' size='XS'>{filter.display}</Title>
                {
                  filter.type === 'range' ?
                    (<Range
                        name={filter.name}
                        minValue={Number(filter.options[0].value)}
                        maxValue={Number(filter.options[1].value)}
                        coef={100}
                      />) :
                    (<List>
                      {filter.options.map((option) => (
                        <ListItem key={option.id}>
                          <Control
                            type={filter.type}
                            name={filter.name}
                            value={option.value}
                            checked={option.isChecked}
                          />
                          <Label>{option.display}</Label>
                        </ListItem>
                      ))}
                    </List>)}
              </Fieldset>
            ))}
          <button type='submit'>Показать</button>
        </Form>
      </Wrapper>
    </StyledFilters>
  )
};

export default Filters;
