import React, { useState } from 'react';
import { Popup } from '../../layout';
import { Title, Input, Button, Alert } from '../../ui';
import { useStore } from "effector-react";
import { $users, loadUsers, findUser } from '../../../model/users';

export interface ILoginForm {
  isOpen: boolean;
  setOpen: any;
}

const LoginForm: React.FC<ILoginForm> = ({ isOpen, setOpen }) => {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const users = useStore($users);

  return (
    <Popup isOpen={isOpen} setOpen={setOpen}>
      <Title as='h2' size='L' marginBottom={60}>Вход</Title>
      {users.loginError ? (
        <Alert width='65%' marginTop={-30} marginBottom={30}>Ошибка входа</Alert>
      ) : null}
      <Input
        width='65%'
        label='Имя:'
        type='text'
        placeholder='Имя Фамилия'
        marginBottom={20}
        value={name}
        onChange={evt => setName(evt.target.value)}
      />
      <Input
        width='65%'
        label='Пароль:'
        type='password'
        placeholder='********'
        value={password}
        onChange={evt => setPassword(evt.target.value)}
      />
      <Button
        marginTop={65}
        highlighted
        onClick={() => {
          loadUsers()
            .then(() => findUser({
              name: name,
              password: password
            }))
            .then(() => {
              if (!users.loginError) setOpen(false);
            });
        }}
      >
        Войти
      </Button>
      <Button marginLeft={45}>Зарегистрироваться</Button>
    </Popup>
  )
}

export default LoginForm;
