import styled from 'styled-components';

interface IRangeScale {
  width?: number;
}

interface IRangeBar {
  left: number;
  right: number;
}

export const RangeScale = styled.div<IRangeScale>`
  position: relative;
  width: ${(props) => props.width ? `${props.width}px` : '100%'};
  height: 2px;
  background-color: ${(props) => props.theme.colors.basicGray};
`;

export const RangeBar = styled.div<IRangeBar>`
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => `${props.left}%`};
  right: ${(props) => `${100 - props.right}%`};
  background-color: ${(props) => props.theme.colors.basicBlack};
`;

export const LeftToggle = styled.button`
  position: absolute;
  top: -50%;
  bottom: -50%;
  left: 0;
  margin: auto;
  width: 18px;
  height: 18px;
  border: 2px solid ${(props) => props.theme.colors.basicBlack};
  border-radius: 50%;
  cursor: pointer;
`;

export const RightToggle = styled(LeftToggle)`
  left: unset;
  right: 0;
`;

export const Label = styled.label`
  max-width: 60px;
  display: inline-block;
  color: ${(props) => props.theme.colors.basicBlack};
  opacity: 0.3;
  font-size: 15px;
`;
