import { createStore, createEffect, createEvent } from 'effector';

export interface IUser {
  name: string;
  password: string;
  email?: string;
  phone?: string;
}

export const loadUsers = createEffect(async () => {
  const req = await fetch('https://jsonplaceholder.typicode.com/users');
  if (req.ok) return req.json();
  else return [];
});

export const findUser = createEvent<IUser>();
export const addUser = createEvent<IUser>();

// @ts-ignore
export const $users = createStore({
  userList: [],
  currentUser: {
    name: ''
  },
  isLogged: false
})
  .on(loadUsers.doneData, (state, payload) => {
    state.userList = payload;
  })
  .on(findUser, (state, newUser) => {
    // @ts-ignore
    if (state.userList.find((user) => user.name === newUser.name)) {
      state.currentUser = newUser;
      state.isLogged = true;
    }
  })
  .on(addUser, (state, newUser) => {
    // @ts-ignore
    state.userList = [...state.userList, newUser];
    state.currentUser = newUser;
    state.isLogged = true;
  });
