import React, { useState } from 'react';
import { useStore } from 'effector-react';
import { $filters, filterProducts } from '../../../model/catalog';
import { filters } from '../../../data/filters';
import { Title, Range } from '../../ui';
import {
  StyledFilters,
  Wrapper,
  Header,
  Form,
  Fieldset,
  List,
  ListItem,
  Control,
  Label
} from './styles';

const Filters: React.FC = () => {
  const currentFilters = useStore($filters);
  const [minPrice, setMinPrice] = useState(currentFilters.price.min);
  const [maxPrice, setMaxPrice] = useState(currentFilters.price.max);
  const [colors, seColors] = useState(currentFilters.colors);
  const [bluetooth, setBluetooth] = useState(currentFilters.bluetooth);

  return (
    <StyledFilters>
      <Wrapper>
        <Header>
          <Title as='h3' size='S'>Фильтр:</Title>
        </Header>
        <Form
          action='#'
          method='get'
          onSubmit={(evt) => {
            evt.preventDefault();
            console.log(evt.target['min-price']);
            filterProducts(currentFilters);
          }}
        >
          {filters.map((filter) => (
            <Fieldset>
              <Title as='legend' size='XS'>{filter.display}</Title>
              {
                filter.type === 'range' ?
                  (<Range
                      name={filter.name}
                      minValue={minPrice}
                      maxValue={maxPrice}
                    />) :
                  (<List>
                    {filter.options!.map((option) => (
                      <ListItem key={option.id}>
                        <Control
                          type={filter.type}
                          name={filter.name}
                          value={option.value}
                          checked={
                            filter.name === 'color' ?
                              false : filter.name === 'bluetooth' ?
                                bluetooth : false
                          }
                          onChange={(evt) =>
                            filter.name === 'color' ?
                              null : filter.name === 'bluetooth' ?
                                setBluetooth(!bluetooth) : null
                          }
                        />
                        <Label>{option.display}</Label>
                      </ListItem>
                    ))}
                  </List>)}
            </Fieldset>))}
          <button type='submit'>Показать</button>
        </Form>
      </Wrapper>
    </StyledFilters>
  )
};

export default Filters;
