import styled from 'styled-components';

export const StyledFilters = styled.section`
  background: linear-gradient(to bottom, 
      ${(props) => props.theme.colors.basicGray} 70px, 
      ${(props) => props.theme.colors.basicLightGray} 0
  );
  height: 100%;
`;

export const Wrapper = styled.div`
  padding: 0 70px 0 200px;
  margin-left: auto;
`;

export const Header = styled.div`
  height: 70px;
  display: grid;
  align-items: center;
`;

export const Form = styled.form`
  margin-top: 90px;
`;

export const Fieldset = styled.fieldset`
  border: none;
  padding: 0 0 70px;
  position: relative;

  &:before {
    content: '';
    position: absolute;
    top: -40px;
    left: 0;
    right: 0;
    height: 2px;
    background-color: ${(props) => props.theme.colors.basicBlack};
  }
`;

export const RangeScale = styled.div`
      position: relative;
      height: 2px;
      margin-bottom: 16px;
      background-color: #DCDCDC;
      margin-top: 20px;
`;

export const RangeBar = styled.div`
      position: absolute;
      height: 2px;
      background-color: #000000;
      left: 0; 
      width: 128px;
`;

export const Toggle = styled.button`
      position: absolute;
      top: -8px;
      min-width: 18px;
      min-height: 18px;
      background: #EBEBEB;
      border: 2px solid #000000;
      border-radius: 50%;
      cursor: pointer;
      margin: 0;
      padding: 0;
`;

export const PriceInput = styled.input`
      padding: 0;
      font: inherit;
      text-align: center;
      background-color: transparent;
      color: inherit;
      border: none;
`;
