import styled from 'styled-components';

export const Header = styled.header`
  padding: ${(props) => props.theme.pagePadding};
  padding-top: 37px;
  padding-bottom: 49px;
`;

export const Wrapper = styled.div`
  height: 100%;
  display: grid;
  grid-template-columns: 1fr 2fr;
`;
