import React from 'react';
import { RangeScale, RangeBar, LeftToggle, RightToggle } from './styles';

export interface IRangeProps {
  width?: number;
  name: string;
  minValue: number;
  maxValue: number;
}

const Range: React.FC<IRangeProps> =
  ({ width,
     name,
     minValue,
     maxValue }) => {
  return (
    <RangeScale width={width}>
      <RangeBar left={minValue / 100} right={maxValue / 100}>
        <div>
          <LeftToggle type='button'/>
          <label>
            от
            <input type='number' name={`min-${name}`} />
          </label>
        </div>
        <div>
          <RightToggle type='button' />
          <label>
            до <input type='number' name={`max-${name}`} />
          </label>
        </div>
      </RangeBar>
    </RangeScale>
  )
};

export default Range;
