import { createGlobalStyle } from 'styled-components';

export const GlobalStyle = createGlobalStyle`

  *,
  *::before,
  *::after {
    box-sizing: border-box;
  }

  html,
  body {
    margin: 0;
    height: 100%;
  }

  body {
    min-width: ${(props) => props.theme.pageWidth};
    background-color: ${(props) => props.theme.basicWhite};
    color: ${(props) => props.theme.basicBlack};
    font-family: ${(props) => props.theme.fontMain};
    font-weight: normal;
    font-size: 18px;
  }

  #root {
    min-height: 100%;
    display: grid;
    grid-template-rows: min-content 1fr min-content;
  }

  .visually-hidden {
    position: absolute;
    width: 1px;
    height: 1px;
    margin: -1px;
    padding: 0;
    border: 0;
    clip: rect(0 0 0 0);
    overflow: hidden;
  }

`;
