import React, { useState }  from 'react';
import { useStore } from 'effector-react';
import { $slides } from '../../../model/slides';
import { Title } from '../../ui';
import { StyledSlider, Slides, Slide, LeftArrow, RightArrow } from './styles';

const Slider: React.FC = () => {
  const slides = useStore($slides);
  const [currentSlide, setCurrentSlide] = useState(0);
  const lastSlide = slides.length - 1;
  const slide = slides[currentSlide];

  return (
    <StyledSlider>
      <h2 className='visually-hidden'>Примеры товаров</h2>
      <Slides>
        <Slide>
          <div>
            <LeftArrow
              type='button'
              onClick={() =>
              currentSlide === 0 ?
                setCurrentSlide(lastSlide) :
                setCurrentSlide(currentSlide - 1)}>
              <span className='visually-hidden'>Предыдущий слайд</span>
            </LeftArrow>
            <img src={slide.image} alt={slide.title} width={560} height={560}/>
            <RightArrow
              type='button'
              onClick={() =>
                currentSlide === lastSlide ?
                  setCurrentSlide(0) :
                  setCurrentSlide(currentSlide + 1)}>
              <span className='visually-hidden'>Следующий слайд</span>
            </RightArrow>
          </div>
          <div>
            <Title as='h3' size='L'>{slide.title}</Title>
            <p>{slide.text}</p>
            <ul>
              {slides.map((_, index) => (
                <li key={index}>
                  <button
                    type='button'
                    onClick={() => setCurrentSlide(index)}
                  />
                </li>
              ))}
            </ul>
          </div>
        </Slide>
      </Slides>
    </StyledSlider>
  )
};

export default Slider;
