import styled from 'styled-components';

export const StyledCard = styled.div`
  display: grid;
  grid-template-columns: 1fr 100px;
  grid-template-areas: 
          'image image'
          'title price';
  gap: 30px;
`;

export const Image = styled.img`
  display: block;
  grid-area: image;
  position: relative;
`;

export const Price = styled.p`
  grid-area: price;
  font-weight: 700
`;
