import React, { ChangeEvent, useState, useEffect, useRef } from 'react';
import {
  RangeScale,
  RangeBar,
  LeftBlock,
  RightBlock,
  LeftToggle,
  RightToggle,
  LeftLabel,
  RightLabel,
  Input
} from './styles';

export interface IRangeProps {
  width?: number;
  name: string;
  minValue: number;
  maxValue: number;
  onMinValueChange: (value: number) => any;
  onMaxValueChange: (value: number) => any;
}

const Range: React.FC<IRangeProps> =
  ({ width,
     name,
     minValue,
     maxValue,
     onMinValueChange,
     onMaxValueChange }) => {

  const [leftEdge, setLeftEdge] = useState(minValue);
  const [rightEdge, setRightEdge] = useState(maxValue);

  const [min, setMin] = useState<string | number>(minValue === 0 ? '' : minValue);
  const [max, setMax] = useState<string | number>(maxValue === 2000 ? '' : maxValue);

  const handleChange = (evt: ChangeEvent<HTMLInputElement>,
                        setValue: (num: string | number) => any,
                        setEdge: (num: number) => any): void => {
    const value = Number(evt.target.value);
    if (value <= 0) setValue('');
    else setValue(value);

    const minRange = Number(evt.target.min);
    const maxRange = Number(evt.target.max);
    if (value < minRange) setEdge(minRange);
    else if (value > maxRange) setEdge(maxRange);
    else setEdge(value);
  };

  const minValueRef = useRef(null);
  const maxValueRef = useRef(null);

  useEffect(()  => {
    console.log(minValueRef.current);
    console.log(maxValueRef.current);
  }, [minValueRef, maxValueRef]);

  return (
    <RangeScale width={width}>
      <RangeBar left={leftEdge} right={rightEdge}>
        <LeftBlock>
          <LeftToggle type='button'/>
          <LeftLabel>
            от
            <Input
              ref={minValueRef}
              type='number'
              min='0'
              max='7000'
              placeholder='0'
              name={`min-${name}`}
              value={min}
              onChange={evt => handleChange(evt, setMin, setLeftEdge)}
            />
          </LeftLabel>
        </LeftBlock>

        <RightBlock>
          <RightToggle type='button' />
          <RightLabel>
            до
            <Input
              ref={maxValueRef}
              type='number'
              min='2000'
              max='10000'
              placeholder='2000'
              name={`max-${name}`}
              value={max}
              onChange={evt => handleChange(evt, setMax, setRightEdge)}
            />
          </RightLabel>
        </RightBlock>
      </RangeBar>
    </RangeScale>
  )
};

export default Range;
