import React from 'react';
import { TabContent, Wrapper } from './styles';

export interface ITabProps {
  id: number;
  title: string;
  text: string;
  icon?: string;
}

const Tab: React.FC<ITabProps> = ({ id, title, text, icon }) => {
  return (
    <TabContent>
      <Wrapper background={icon ? icon : ''}>
        <h3>{title}</h3>
        <p>{text}</p>
      </Wrapper>
    </TabContent>
  )
};

export default Tab;
