import { createStore, createEvent } from 'effector';
import { devices, IDevice } from '../data/devices';

export const sortByLowestPrice = createEvent();
export const sortByHighestPrice = createEvent();
export const sortByNovelty = createEvent();

export const $catalog = createStore<IDevice[]>(devices)
  .on(sortLowToHighPrice, (products, type) => {
    switch (type) {
      case 'lowToHigh':
        return products.sort((a, b) => a.price - b.price);
      case 'highToLow':
        return products.sort((a, b) => b.price - a.price);
      default:
        return products;
    }
  }).on(sortByNovelty, (products) => {
    products.sort((a, b) => a.isNew ? 1 : b.isNew ? 0 : -1);
  });
