import React, { ChangeEvent, useState, useEffect, useRef } from 'react';
import {
  RangeScale,
  RangeBar,
  LeftBlock,
  RightBlock,
  LeftToggle,
  RightToggle,
  LeftLabel,
  RightLabel,
  Input
} from './styles';

export interface IRangeProps {
  width?: number;
  name: string;
  minValue: number;
  maxValue: number;
  onMinValueChange: (value: number) => any;
  onMaxValueChange: (value: number) => any;
}

const Range: React.FC<IRangeProps> =
  ({ width,
     name,
     minValue,
     maxValue,
     onMinValueChange,
     onMaxValueChange }) => {

  return (
    <RangeScale width={width}>
      <RangeBar left={leftEdge} right={rightEdge}>
        <LeftBlock>
          <LeftToggle type='button'/>
          <LeftLabel>
            от
            <Input
              ref={minValueRef}
              type='number'
              min='0'
              max='7000'
              placeholder='0'
              name={`min-${name}`}
              value={min}
              onChange={evt => handleChange(evt, setMin, setLeftEdge)}
            />
          </LeftLabel>
        </LeftBlock>

        <RightBlock>
          <RightToggle type='button' />
          <RightLabel>
            до
            <Input
              ref={maxValueRef}
              type='number'
              min='2000'
              max='10000'
              placeholder='2000'
              name={`max-${name}`}
              value={max}
              onChange={evt => handleChange(evt, setMax, setRightEdge)}
            />
          </RightLabel>
        </RightBlock>
      </RangeBar>
    </RangeScale>
  )
};

export default Range;
