import React from 'react';
import { filters } from '../../../data/filters';
import { Title } from '../../ui';
import {
  StyledFilters,
  Wrapper,
  Header,
  Form,
  Fieldset,
  RangeScale,
  RangeBar,
  Toggle,
  PriceInput
} from './styles';

const Filters: React.FC = () => {
  return (
    <StyledFilters>
      <Wrapper>
        <Header>
          <Title as='h3' size='S'>Фильтр:</Title>
        </Header>
        <Form action='#' method='get'>
          {
            filters.map((filter) => (
              <Fieldset>
                <Title as='legend' size='XS'>{filter.display}</Title>
                {
                  filter.type === 'range' ?
                    (<div>
                      <RangeScale>
                        <RangeBar>
                          {
                            filter.options.map((option) => (
                              <>
                                <Toggle type='button'>
                                  <span className='visually-hidden'>Изменить стоимость</span>
                                </Toggle>
                                <label>
                                  {option.display}
                                  <PriceInput
                                    type='number'
                                    name={option.name}
                                    value={option.value}
                                  />
                                </label>
                              </>
                            ))
                          }
                        </RangeBar>
                      </RangeScale>
                    </div>) :
                    (<ul>
                      {filter.options.map((option) => (
                        <li>
                          <label className="control">
                            <input
                              type={filter.type}
                              name={filter.name}
                              value={option.value}
                              checked={option.isChecked}
                            />
                            <span>{option.display}</span>
                          </label>
                        </li>
                      ))}
                    </ul>)}
              </Fieldset>
            ))}
          <button type='submit'>Показать</button>
        </Form>
      </Wrapper>
    </StyledFilters>
  )
};

export default Filters;
