import  styled from 'styled-components';

export const StyledFooter = styled.footer`
  background-color: ${(props) => props.theme.colors.basicDark};
  padding: ${(props) => props.theme.pagePadding};
  padding-top: 62px;
  padding-bottom: 64px;
  display: grid;
  grid-template-areas: 
    'logo . .'
    'address nav phone'
    '. socials copyright';
`;
