import styled from 'styled-components';

interface ISearch {
  width?: string;
};

export const StyledSearch = styled.form<ISearch>`
  width: ${(props) => props.width ? props.width : 'min-content'};
  display: flex;
`;

export const Input = styled.input`
  background-color: transparent;
  border: none;
  flex-grow: 1;
`;
