import styled from 'styled-components';

export const RangeScale = styled.div`
  position: relative;
  width: ${(props) => props.width ? `${props.width}px` : '100%'};
  height: 2px;
  background-color: ${(props) => props.theme.colors.basicLightGray};
`;
