import styled from 'styled-components';

export const StyledTabs = styled.section`
  background: linear-gradient(to bottom, transparent 100px, ${(props) => props.theme.colors.basicLight} 0);
  padding: ${(props) => props.theme.pagePadding};
  padding-top: 0;
  padding-bottom: 70px;
  display: grid;
  grid-template-columns: ${(props) => props.theme.layoutColumnWide} 1fr;
  column-gap: 120px;
`;

export const TabsNav = styled.ul`
  list-style-type: none;
  padding: 0;
`;

export const Content = styled.div`

`;
