import styled from 'styled-components';

interface ISearch {
  width?: string;
};

export const StyledSearch = styled.form<ISearch>`
  display: flex;
  width: ${(props) => props.width ? props.width : 'min-content'};
  font-family: ${(props) => props.theme.fontMain};
  font-size: ${(props) => props.theme.fontSize.XS};
  line-height: ${(props) => props.theme.lineHeight.XS};
  position: relative;
`;

export const Button = styled.button`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  width: 160px;
  text-align: center;
  border: none;
  background: none;
  cursor: pointer;
  display: none;
`;

export const Input = styled.input`
  flex-grow: 1;
  background: none;
  border: 2px solid transparent;
  border-radius: 20px;
  padding: 10px 180px 10px 52px;
  margin: 0;

  &::placeholder {
    color: ${(props) => props.theme.colors.basicBlack};
    opacity: 0.3;
  }

  &:hover::placeholder {
    opacity: 0.6;
  }

  &:focus {
    outline: none;
    border: 2px solid ${(props) => props.theme.colors.themeAdditional};
    &::placeholder {
      opacity: 0.3;
    }
    & + ${Button} {
      display: block;
    }
  }
`;
