import React from 'react';
import { filters } from '../../../data/filters';
import { Title, Range } from '../../ui';
import {
  StyledFilters,
  Wrapper,
  Header,
  Form,
  Fieldset,
  RangeScale,
  RangeBar,
  Toggle,
  PriceLabel,
  PriceInput
} from './styles';

const Filters: React.FC = () => {
  return (
    <StyledFilters>
      <Wrapper>
        <Header>
          <Title as='h3' size='S'>Фильтр:</Title>
        </Header>
        <Form action='#' method='get'>
          {
            filters.map((filter) => (
              <Fieldset>
                <Title as='legend' size='XS' marginBottom={20}>{filter.display}</Title>
                {
                  filter.type === 'range' ?
                    (<>
                      <Range
                        name={filter.name}
                        minValue={Number(filter.options[0].value)}
                        maxValue={Number(filter.options[1].value)}
                      />
                      <div className='visually-hidden'>
                        <RangeScale>
                          <RangeBar>
                            {
                              filter.options.map((option) => (
                                <>
                                  <Toggle type='button'>
                                    <span className='visually-hidden'>Изменить стоимость</span>
                                  </Toggle>
                                  <PriceLabel>
                                    {option.display}
                                    <PriceInput
                                      type='number'
                                      name={option.name}
                                      value={option.value}
                                    />
                                  </PriceLabel>
                                </>
                              ))
                            }
                          </RangeBar>
                        </RangeScale>
                      </div>
                      </>) :
                    (<ul>
                      {filter.options.map((option) => (
                        <li key={option.id}>
                          <label className="control">
                            <input
                              type={filter.type}
                              name={filter.name}
                              value={option.value}
                              checked={option.isChecked}
                            />
                            <span>{option.display}</span>
                          </label>
                        </li>
                      ))}
                    </ul>)}
              </Fieldset>
            ))}
          <button type='submit'>Показать</button>
        </Form>
      </Wrapper>
    </StyledFilters>
  )
};

export default Filters;
