import React from 'react';
import Tab from './tab';
import { ITab } from '../../../data/tabs';
import { useStore } from 'effector-react';
import { StyledTabs, TabsNav } from './styles';

const Tabs: React.FC<{ tabs: ITab[] }> = ({ tabs }) => {
  const activeTab = useStore($activeTab);
  return (
    <StyledTabs>
      <h2 className='visually-hidden'>Сервисы</h2>
      <TabsNav>
        {tabs.map((tab) => (
          <li key={tab.id}>
            {tab.title}
          </li>
        ))}
      </TabsNav>
      <Tab tab={tabs[0]}/>
    </StyledTabs>
  )
};

export default Tabs;
