import styled from 'styled-components';
import { IRangeProps } from './range';

export const RangeScale = styled.div<IRangeProps>`
  position: relative;
  width: ${(props) => props.width ? `${props.width}px` : '100%'};
  height: 2px;
  background-color: ${(props) => props.theme.colors.basicGray};
`;

export const RangeBar = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  background-color: ${(props) => props.theme.colors.basicBlack};
`;
