import React from 'react';
import { filters } from '../../../data/filters';
import { Title } from '../../ui';
import {
  StyledFilters,
  Wrapper,
  Header,
  Form,
  Fieldset
} from './styles';

const Filters: React.FC = () => {
  return (
    <StyledFilters>
      <Wrapper>
        <Header>
          <Title as='h3' size='S'>Фильтр:</Title>
        </Header>
        <Form action='#' method='get'>
          {
            filters.map((filter) => (
              <Fieldset>
                <Title as='legend' size='XS'>{filter.display}</Title>
                {
                  filter.type === 'range' ?
                    (<div className="range">
                      <div className="range-scale">
                        <div className="range-bar">
                          <button className="range-toggle toggle-min" type="button">
                            <span className='visually-hidden'>Изменить минимальную цену</span>
                          </button>
                          <label className="range-toggle-label range-from">
                            от <input className="range-input" type="number" value="0" name="min-price" />
                          </label>
                          <button className="range-toggle toggle-max" type="button">
                            <span className='visually-hidden'>Изменить максимальную цену</span>
                          </button>
                          <label className="range-toggle-label range-to">
                            до <input className="range-input" type="number" value="5000" name="max-price" />
                          </label>
                        </div>
                      </div>
                    </div>) :
                    (<ul>
                      {filter.options.map((option) => (
                        <li>
                          <label className="control">
                            <input
                              type={filter.type}
                              name={filter.name}
                              value={option.value}
                              checked={option.isChecked}
                            />
                            <span>{option.display}</span>
                          </label>
                        </li>
                      ))}
                    </ul>)
                }
              </Fieldset>
            ))
          }
          <button type='submit'>Показать</button>
        </Form>
      </Wrapper>
    </StyledFilters>
  )
};

export default Filters;
