import styled from 'styled-components';
import { Logo, Search, Nav, UserNav } from '../../ui';
import { Link } from 'react-router-dom';

export const StyledHeader = styled.header`
  padding: ${(props) => props.theme.pagePadding};
  padding-top: 0;
  padding-bottom: 0;
`;

export const Wrapper = styled.div`
  background: linear-gradient(to bottom, transparent 40px, ${(props) => props.theme.colors.themeMain} 0);
  padding: 17px 60px 38px;
  display: grid;
  grid-template-areas: 
          'logo .'
          'search user-nav' 
          'catalog-menu nav';
  grid-template-columns: 1fr 1fr;
  grid-template-rows: min-content;
  column-gap: ${(props) => props.theme.layoutGap};
  row-gap: 20px;
`;

export const StyledLogo = styled(Logo)`
  grid-area: logo;
`;

export const StyledSearch = styled(Search)`
  grid-area: search;
  margin-left: -20px;
`;

export const StyledUserNav = styled(UserNav)`
  grid-area: user-nav;
  margin-left: -20px;
`;

export const StyledNav = styled(Nav)`
  grid-area: nav;
  margin-left: -20px;
`;

export const CatalogNavHeader = styled(Link)`
  grid-area: catalog-menu;
  font-family: ${(props) => props.theme.fontAdditional};
  font-size: ${(props) => props.theme.fontSize.S};
  line-height: ${(props) => props.theme.lineHeight.S};
  letter-spacing: ${(props) => props.theme.letterSpacing};
  font-weight: 800;
  text-decoration: none;
  text-transform: uppercase;
  color: inherit;
  display: block;
  padding: 10px 0;
  border: 2px solid transparent;
  border-radius: 20px;
  cursor: pointer;
  width: 300px;
  position: relative;

  &:before,
  &:after {
    content: '';
    position: absolute;
    right: 0;
    top: 0;
    bottom: 0;
    margin: auto;
    width: 16px;
    height: 2px;
    background-color: black;
  }

  &:after {
    transform: rotate(90deg);
  }
`;
