import React, { useState } from 'react';
import {
  RangeScale,
  RangeBar,
  LeftBlock,
  RightBlock,
  LeftToggle,
  RightToggle,
  LeftLabel,
  RightLabel,
  Input
} from './styles';

export interface IRangeProps {
  width?: number;
  name: string;
  minValue: number;
  maxValue: number;
  coef: number;
}

const Range: React.FC<IRangeProps> =
  ({ width,
     name,
     minValue,
     maxValue,
     coef}) => {

  const [min, setMin] = useState(minValue / coef);
  const [max, setMax] = useState(100 - maxValue / coef);

  return (
    <RangeScale width={width}>
      <RangeBar left={min} right={max}>
        <LeftBlock>
          <LeftToggle type='button'/>
          <LeftLabel>
            от <Input type='number' name={`min-${name}`} value={minValue} />
          </LeftLabel>
        </LeftBlock>

        <RightBlock>
          <RightToggle type='button' />
          <RightLabel>
            до <Input type='number' name={`max-${name}`} value={maxValue} />
          </RightLabel>
        </RightBlock>
      </RangeBar>
    </RangeScale>
  )
};

export default Range;
