import React from 'react';
import {
  RangeScale,
  RangeBar,
  LeftBlock,
  RightBlock,
  Toggle,
  Label,
  Input
} from './styles';

export interface IRangeProps {
  width?: number;
  name: string;
  minValue: number;
  maxValue: number;
}

const Range: React.FC<IRangeProps> =
  ({ width,
     name,
     minValue,
     maxValue }) => {
  return (
    <RangeScale width={width}>
      <RangeBar left={minValue / 100} right={maxValue / 100}>
        <LeftBlock>
          <Toggle type='button'/>
          <Label>
            от <Input type='number' name={`min-${name}`} value={minValue} />
          </Label>
        </LeftBlock>
        <RightBlock>
          <Toggle type='button' />
          <Label>
            до <Input type='number' name={`max-${name}`} value={maxValue} />
          </Label>
        </RightBlock>
      </RangeBar>
    </RangeScale>
  )
};

export default Range;
