import styled from 'styled-components';

export const StyledTable = styled.dl`
  display: grid;
  grid-template-columns: repeat(auto-fill, 160px);
`;

export const Key = styled.dd`
  color: #444444;
  font-size: 16px;
  grid-row: 2 / 3;
`;

export const Value = styled.dt`
  font-size: 36px;
  grid-row: 1 / 2;
  margin: 0;
  padding-bottom: 10px;
`;
