import  styled from 'styled-components';

export const StyledFooter = styled.footer`
  background-color: ${(props) => props.theme.basicDark};
  padding: ${(props) => props.theme.pagePadding};
  padding-top: 62px;
  padding-bottom: 64px;
`;
