import React from 'react';
import { filters } from '../../../data/filters';
import { Title, Range } from '../../ui';
import {
  StyledFilters,
  Wrapper,
  Header,
  Form,
  Fieldset
} from './styles';

const Filters: React.FC = () => {
  return (
    <StyledFilters>
      <Wrapper>
        <Header>
          <Title as='h3' size='S'>Фильтр:</Title>
        </Header>
        <Form action='#' method='get'>
          {
            filters.map((filter) => (
              <Fieldset>
                <Title as='legend' size='XS'>{filter.display}</Title>
                {
                  filter.type === 'range' ?
                    (<Range
                        name={filter.name}
                        minValue={Number(filter.options[0].value)}
                        maxValue={Number(filter.options[1].value)}
                        coef={100}
                      />) :
                    (<ul>
                      {filter.options.map((option) => (
                        <li key={option.id}>
                          <label className="control">
                            <input
                              type={filter.type}
                              name={filter.name}
                              value={option.value}
                              checked={option.isChecked}
                            />
                            <span>{option.display}</span>
                          </label>
                        </li>
                      ))}
                    </ul>)}
              </Fieldset>
            ))}
          <button type='submit'>Показать</button>
        </Form>
      </Wrapper>
    </StyledFilters>
  )
};

export default Filters;
