import styled from 'styled-components';
import { IPopup } from './popup';

export const StyledPopup = styled.div<IPopup>`
  display: ${(props) => props.isOpen ? 'block' : 'none'};
  background-color: ${(props) => props.theme.colors.basicBlack};
  color: ${(props) => props.theme.colors.basicWhite};
  padding: 20px;
  box-shadow: 0 10px 0 0 #0000001A;
  min-height: 100%;
  transform: translate(60px, 14px);
  font-size: 16px;
  line-height: 20px;
  width: 280px;
  position: absolute;
  top: 30px;
  right: 0;
  z-index: 3;

  &:before {
    content: "";
    background: transparent url('images/arrows/thin-triangle.svg') no-repeat center;
    transform: translate(-3px, -20px);
    display: block;
    height: 6px;
    width: 280px;
  }
`;
